mapper.select:
SELECT COUNT(*)
FROM transaction t
WHERE t.buyer_id = #{value}

SELECT MAX(t.tran_status_code)
FROM transaction t
WHERE t.prod_no = #{value}

mapper.update:
UPDATE transaction
   SET tran_status_code = #{tranStatusCode}
 WHERE tran_no = #{tranNo, jdbcType=NUMERIC}

UPDATE transaction
   SET tran_status_code = #{tranStatusCode}
 WHERE prod_no = #{prodNo, jdbcType=NUMERIC}

mapper.select:
SELECT tran_status_code
FROM transaction
WHERE tran_no = #{tranNo}
